mapper.insert:
INSERT INTO corona_world
(areaNm, createDt, natDeathCnt, natDeathRate, natDefCnt, nationNm, nationNmEn)
VALUES(#{areaNm}, #{createDt}, #{natDeathCnt}, #{natDeathRate}, #{natDefCnt}, #{nationNm}, #{nationNmEn});

mapper.select:
select sum(natDefCnt) as natDefCnt,sum(natDeathCnt)as natDeathCnt 
from corona_world cw 
where date_format(createDt , "%Y-%m-%d") = str_to_date(#{date}, "%Y-%m-%d");

select sum(natDefCnt) as natDefCnt,sum(natDeathCnt) as natDeathCnt 
from corona_world 
where date_sub(date_format(createDt,"%Y-%m-%d"), interval -1 day) =  str_to_date(#{date}, "%Y-%m-%d");

select date_format(createDt,"%Y-%m-%d")as chartDate, sum(natDefCnt) as natDefCnt ,sum(natDeathCnt) as natDeathCnt from corona_world
where date_format(createDt,"%Y-%m-%d") between date_sub(date_format(#{date},"%Y-%m-%d"),interval 1 month) and date_format(#{date}, "%Y-%m-%d") group by date_format(createDt,"%Y-%m-%d") order by date_format(createDt,"%Y-%m-%d") asc;
where date_format(createDt,"%Y-%m-%d") between date_sub(date_format(#{date},"%Y-%m-%d"),interval 3 month) and date_format(#{date}, "%Y-%m-%d") group by date_format(createDt,"%Y-%m-%d") order by date_format(createDt,"%Y-%m-%d") asc;
group by date_format(createDt,"%Y-%m-%d") order by date_format(createDt,"%Y-%m-%d") asc;
